 Request = (method , obj) => {

     const xhr = new XMLHttpRequest();
     xhr.open(method , './server/model/loginAction.php', false);
     xhr.setRequestHeader('Content-Type', 'application/json');
     xhr.send(JSON.stringify(obj));
     const answer = JSON.parse(xhr.responseText);
     let response

     switch(xhr.status){

         case 200 || 201:   response = JSON.parse(xhr.responseText);
             break;

         default :   console.log( xhr.status + ': ' + xhr.statusText );

     }

   return response
}

export default Request