import SignUpContainer from './component.js'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import {browserHistory} from 'react-router'
import Lending from 'containers/lending'
import Menu from '../menu'


class SignUp extends Component {

    render(){
     this.props.hideButton ? browserHistory.push('/profile-balance') : null
        return (
            <div>
                <div className="personal-header">
                    <div className="header-overlay">
                <Menu />
                </div>
                </div>
                <br />
                {/*menu*/}
                <div className='flex-container'>
                    {this.props.hideButton ? <Lending /> : <SignUpContainer />}
                </div>
                <br />
                <div className="footer">
                    <div className="wrapper">
                        <div className="footer-container">
                            <div className="footer-menu">
                                <span className="footer-menu-title">Help &amp; Support</span>
                                <button className="footer-menu-btn">read more</button>
                            </div>{/* footer-menu */}
                            <div className="footer-menu">
                                <span className="footer-menu-title">FAQ</span>
                                <button className="footer-menu-btn">read more</button>
                            </div>{/* index-footer-menu */}
                        </div>{/* index-footer-container */}
                        <p className="footer-text">Packages for mobile, IoT, front end, back end, robotics… everything you need to start building amazing things.</p>
                    </div>{/* wrapper */}
                    <p className="footer-copyright">© 2013 – 2022, BAAX | capital</p>
                </div>{/* index-footer */}
            </div>

        )
    } 
}

const mapStateToProps = state => ({
   hideButton : state.auth.hideButton
})

export default connect(mapStateToProps, null)(SignUp)

