import {AUTH, SIGN_OUT} from 'actionsTypes'
import R from 'ramda'

const initialState = {
}

export default (state = initialState, {type, hideButton, UserId}) => {

    switch(type) {

        case 'AUTH' :  //Когда уберется релоад страниц это убрать
            ///////////////////////////////////////////
            //localStorage['do-go'] = JSON.stringify({UserId : UserId});
            //window.location = "/"
            ///////////////////////////////////////////

            return R.merge(state, {
                hideButton : hideButton,
                UserId : UserId
            })

            break;

        case 'SIGN_OUT' :  //Когда уберется релоад страниц это убрать
            ///////////////////////////////////////////
            //localStorage['do-go']= ''
            //window.location = "/"
            ///////////////////////////////////////////

            return R.merge(state, {
                hideButton : hideButton

            })
            break;
          default :   return state
    }
}




