import R from 'ramda'
import {FETCH_PRICE_SUCCESS} from 'actionsTypes'


const initialState = {}

export default (state = initialState,{type,payload}) => {
    switch (type) {
        case FETCH_PRICE_SUCCESS:
            const newValue = R.indexBy(R.prop('id'),payload)
            return R.merge(newValue,state)
        default:
            return state
    }
}

