import R from 'ramda'

import {
    FETCH_PRICE_SUCCESS
} from 'actionsTypes'

const initialState={
    ids:[]
}
export default (state=initialState,{type,payload})=>{
    switch (type) {
        case FETCH_PRICE_SUCCESS:
            return R.merge(state, {
                ids: R.pluck('id', payload)
            })
        default:
            return state
    }
}