import R from 'ramda'

export const getCryptoCurrencyById = (state, id) => R.prop(id, state.lending)

export const getCoin = (state , id) => R.prop(id, state.coin)

export const getCryptoCurrency = state => {
    const prices = R.map(id => getCryptoCurrencyById(state, id), state.lendingPage.ids)
    return prices
}